<style lang="scss">
	.navbar {
		height: 4rem;
		position: sticky;
		top: 0;
		width: 100%;
		background: var(--background);
		display: flex;
		align-items: center;
		justify-content: center;
		z-index: 5;

		@media (max-width: 50rem) {
			position: fixed;
			top: unset;
			bottom: 0;
		}
	}

	.inner {
		width: 77rem;
		display: grid;
		align-items: center;
		grid-template-columns: 1fr 1fr 1fr;
		padding: 0 1rem;
	}

	.nav {
		display: flex;
		justify-self: center;
		gap: 0.5rem;
	}

	.nav-link {
		padding: 0 0.75rem;
		height: 2.25rem;
		border-radius: 0.5rem;
		display: flex;
		align-items: center;
		justify-content: center;
		color: var(--foreground-secondary);
		font-weight: 500;
		text-decoration: none;
		transition: 0.2s;
		background: var(--background);

		&:hover {
			background: var(--background-hover);
		}
	}

	.social-links {
		justify-self: end;
		display: flex;
		gap: 0.75rem;
	}
	
	.social-link {
		color: var(--foreground-subtle);
	}
</style>
